import React, { useEffect, useRef } from "react";
import styles from "./App.module.css";

const size = 6;
const steps = 50;
const n = 50;
const data = Array(100).map(_ => 0);
data[n / 2] = 1;

const getRules = (rules_id: number) => {
  return (s: Array<number>, i: number) => {
    const limit = s.length - 1;
    const n1 = i === 0 ? limit : i - 1;
    const n2 = i === limit ? 0 : i + 1;

    const id = (s[n1] << 2) + (s[i] << 1) + (s[n2] << 0);
    return (rules_id >> id) & 1;
  };
};

const getLine = (space: Array<number>, rules_id: number) => {
  const rules = getRules(rules_id);
  return space.map((_, i) => rules(space, i));
};

const App = () => {
  const canvas = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const drawer = canvas.current?.getContext("2d");
    if (!drawer) return;

    drawer.fillStyle = "white";


    for (let currentLine = 0; currentLine < steps; ++currentLine) {
      for (let i = 0, current = 0; i < data.length; ++i) {
        if (data[i]) drawer.fillRect(current, currentLine * size, size, size);
        current += size;
      }
    }
  }, []);

  return (
    <div className={styles.app}>
      <main className={styles.appMain}>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>

        <canvas ref={canvas} width="400" height="400" />
      </main>
    </div>
  );
};

export default App;
