import React, { useEffect, useRef } from "react";
import styles from "./App.module.css";

import { getLine, getRules } from "./automata";

const rule = 54;
const size = 2;
const steps = 500;
const n = 500;
const data = Array(n);
for (let i = 0; i < data.length; ++i) data[i] = 0;
data[n / 2] = 1;

const App = () => {
  const canvas = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const drawer = canvas.current?.getContext("2d");
    if (!drawer) return;

    drawer.fillStyle = "white";

    for (let lineNum = 0, line = data; lineNum < steps; ++lineNum) {
      line.forEach((cell, index) => {
        if (cell) drawer.fillRect(index * size, lineNum * size, size, size);
      });

      console.log(line);
      line = getLine(line, rule);
    }
  }, []);

  return (
    <div className={styles.app}>
      <main className={styles.appMain}>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>

        <canvas
          ref={canvas}
          width={size * n}
          height={size * steps}
          style={{ border: "solid", margin: "2rem" }}
        />
      </main>
    </div>
  );
};

export default App;
