import React, { useEffect, useRef, useState } from "react";
import styles from "./App.module.css";

import { getLine, getRules } from "./automata";

const rule = 54;
const size = 4;
const steps = 200;
const n = 200;
const data = Array(n).fill(0);
data[n / 2] = 1;

const App = () => {
  const canvas = useRef<HTMLCanvasElement>(null);
  const [count, setCount] = useState(0);
  const [rule, setRule] = useState(0);

  useEffect(() => {
    const drawer = canvas.current?.getContext("2d");
    if (!drawer) return;

    drawer.fillStyle = "white";

    for (let step = 0, line = data; step < steps; ++step) {
      line.forEach((cell, index) => {
        if (!cell) return;
        drawer.fillRect(index * size, step * size, size, size);
      });

      line = getLine(line, rule);
    }
  }, [count]);

  return (
    <div className={styles.app}>
      <main className={styles.appMain}>
        <p>Cellular Automata</p>

        <label htmlFor="rule">Rule: </label>
        <input id="rule" type="number" min="0" max="255" value={rule} onChange={e => setRule(e.target.value)} />

        <button onClick={() => setCount(c => c + 1)} />

        <canvas
          ref={canvas}
          width={size * n}
          height={size * steps}
          style={{ border: "solid", margin: "2rem" }}
        />
      </main>
    </div>
  );
};

export default App;
